import React,{useContext} from 'react'
import CartContext from '../../store/cart-context'
import Modal from '../UI/Modal'
import classes from './Cart.module.css'
import CartItem from './CartItem'

function Cart(props) {
    const cartCtx = useContext(CartContext);
    const total_amt = `$${cartCtx.total_amt.toFixed(2)}`;
    const cart_items =   (     <ul className={classes['cart-items']}>
    {cartCtx.items.map( item =>{
        <CartItem name={item.name} key={item.id} price={item.price} amt={item.amt} />
    })} 
</ul> )
  return (
    <Modal onhideCartHandler={props.onhideCartHandler}>
        {cart_items}
        <div className={classes.total}>   
            <span>Total Amount</span>
            <span>{total_amt}</span>
        </div>
        <div className={classes.actions}>
            <button className={classes['button--alt']} onClick={props.onhideCartHandler}>Close</button>
            {cartCtx.items.length>0 && <button className={classes.button}>Order</button>}
        </div>
    </Modal>
  )
}

export default Cart
